    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String uID=user.getText();
        String p=new String(pass.getPassword());
        Main_GUI frame1=new Main_GUI();
        if(uID.equalsIgnoreCase("Admin") && p.equals("iss")){
            frame1.setVisible(true);
            uID="Admin";
            frame1.uID=uID;
            this.dispose();
        }
        else if(uID.equalsIgnoreCase("User") && p.equals("tanu")){
            frame1.setVisible(true);
            uID="User";
            frame1.uID=uID;
            this.dispose();
        }
        else if(uID.equalsIgnoreCase("") || p.equals("")){
            JOptionPane.showMessageDialog(this,"Enter valid username and password","Access Denied",0);
        }
        else{
            JOptionPane.showMessageDialog(this,"Invalid username or password","Access Denied",0);
            user.setText("");
            pass.setText("");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

